import cl from './ProductPage.module.scss'
import React, { useState } from 'react'
import { useParams } from 'react-router-dom'
import { useFetching } from '../../hooks/useFetching';
import axios from 'axios';
import { useEffect } from 'react';

export const ProductPage = () => {
    const { product } = useParams();
    const [prod, setProd] = useState({})
    const [getProduct, isLoading, error] = useFetching(async () => {
        try {
            const res = await axios.get(`https://fakestoreapi.com/products/${product}`)
            console.log(res)
            setProd(res.data)
        } catch (err) {
            console.log(err);
        }
    })
    useEffect(() => {
        getProduct()
    }, [])

    return (
        <div className={cl.product_wrapper}>
            <div className={cl.product_img_wrapper}>
                <img className={cl.product_img} src={prod.image} alt="product_img" />
            </div>
            <div></div>
        </div>
    )
}
