import React, { useEffect, useState } from 'react'
import { BackAside } from '../../components/UI/BackAside/BackAside';
import { basket } from '../../stores/BasketStore'
import cl from './Basket.module.scss'
import { observer } from 'mobx-react-lite'


const Basket = observer(() => {
    const { basketList, removeFromTheBasket } = basket;
    const [booleans, setBooleans] = useState([])
    useEffect(() => setBooleans(basketList.map(() => true
    )), [])
    const theFinalBasket = [...basketList];
    theFinalBasket
        .forEach((prod, index) => prod.checked = booleans[index])

    const loh = theFinalBasket.filter(i => i.checked === true)

    console.log(loh);


    return (
        <>
            <BackAside />
            <div className={cl.basket_body}>
                <ul className={cl.items}>
                    {basketList.map((product, index) => {
                        return <li key={product.id} className={cl.items__item}>
                            <div>
                                <input
                                    onChange={() => {
                                        let arr = [...booleans]
                                        arr[index] = !booleans[index];
                                        setBooleans(arr)
                                    }}
                                    checked={booleans.length > 0 ? booleans[index] : true}
                                    type="checkbox"
                                    id={product.id}
                                    className={cl.items__input}
                                />
                                <label className={cl.items__checkbox} htmlFor={product.id}>skdjfgbkladfhbgkajdfn</label>
                            </div>
                        </li>
                    }
                    )}
                </ul>
                <button>оформить заказ</button>
            </div>

        </>

    )
})

export { Basket }
