import { observer } from 'mobx-react-lite'
import React from 'react'
import { productStore } from '../../../stores/ProductStore'
import cl from './TypeBar.module.scss'

export const TypeBar = observer(() => {
    const { categories, category } = productStore;
    const getClassForLi = () => {
        if (category === type) {
            return `${cl.list__item}${cl.active}`
        } else {
            return `${cl.list__item}`
        }
    }
    const getClassForSpan = () => {
        return cl.list__item_span
    }


    return (
        <aside className={cl.aside}>
            <ul className={cl.list}>
                {categories.map(type =>
                    <li className={getClassForLi()} key={category}>
                        <span className={getClassForSpan()}>
                            {type.toUpperCase()}
                        </span>
                    </li>)}
            </ul>
        </aside>

    )
})
